import React, { useEffect, useState } from "react";
import axios from 'axios';
import CharacterCard from './components/characterCard';

export default function SearchMarvel() {

    const searchMarvelContent = async (e) => {
        e.preventDefault();
        console.log("submitting"); 
        characterCard()
    }

    const characterURL = 'https://gateway.marvel.com:443/v1/public/characters';

    const credentials = '?ts=1&apikey=6871af630ca51742153d2db8dbf10dcb&hash=609bb5211dbf0e54c53e927bb92f5ee7';

    const [characterData, setCharacterData] = useState([]);

    useEffect(() => {
        axios.get(characterURL + credentials).then((response) => {
            console.log(response.data.data.results)
            setCharacterData(response.data.data.results);
        })
    },[])


    return (
        <form className="form" onSubmit={searchMarvelContent} >
            <label className="label" htmlFor="query">Comic Name</label>
            <input className="input" type="text" name="query" placeholder="i.e. Spider-Man" id="searchInput"></input>
            <button className="button" type="submit">Search</button>
        </form>
    )
}
